import React, { useState, useEffect } from 'react';
import UserService from '../services/UserService';
import { useNavigate } from 'react-router-dom';

const ViewTaskComponent = ({ taskIdentifier }) => {
    const navigate = useNavigate();
    const buttonContainerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
      };
      const buttonStyle = {
        margin: '10px 20px',
        padding: '15px 20px',
        fontSize: '16px',
        borderRadius: '5px',
        border: 'none',
        cursor: 'pointer',
      };
    const buttonView = {
        ...buttonStyle,
    backgroundColor: '#3498db', // Blue color
    color: '#fff', // White text
      };

  const [task, setTask] = useState({
    id: '',
    taskName: '',
    startDate: '',
    endDate: '',
    taskStatus: '',
    totalEffortReq: '',
  });

  const viewAllTask = () => {
    navigate(`/list/all`);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        let response;
        if (isNaN(taskIdentifier)) {
          // If taskIdentifier is not a number, fetch by taskName
          response = await UserService.getTaskByTaskName(taskIdentifier);
        } else {
          // If taskIdentifier is a number, fetch by taskID
          response = await UserService.getTaskById(taskIdentifier);
        }
        setTask(response.data);
      } catch (error) {
        console.error('Error fetching task:', error);
        // Handle error, e.g., show an error message to the user
      }
    };

    fetchData();
  }, [taskIdentifier]);

  return (
    <div>
      <br></br>
      <div className="card col-md-6 offset-md-3">
        <h3 className="text-center">Task Details</h3>
        <div className="card-body">
          <div className="row">
            <label>Task ID</label>
            <div> {task.id}</div>
          </div>
          <div className="row">
            <label>Task Name</label>
            <div> {task.taskName}</div>
          </div>
          <div className="row">
            <label>Start Date</label>
            <div> {task.startDate}</div>
          </div>
          <div className="row">
            <label>End Date</label>
            <div> {task.endDate}</div>
          </div>
          <div className="row">
            <label>Task Status</label>
            <div> {task.taskStatus}</div>
          </div>
          <div className="row">
            <label>Total Effort Required</label>
            <div> {task.totalEffortReq}</div>
          </div>
        </div>
      </div>
      <div style={buttonContainerStyle}>
      <div>
      <button style={buttonView} onClick={viewAllTask}>
        ViewAllTask</button>
      </div>
      </div>
    </div>
  );
};

export default ViewTaskComponent;
