import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import UserService from '../services/UserService';

const UpdateUserComponent = () => {
  const [taskStatus, setTaskStatus] = useState('');
  const navigate = useNavigate();
  const updateTask = useParams();


  useEffect(() => {
    // Fetch the existing task status when the component mounts
    const fetchTaskStatus = async () => {
      try {
        const response = await UserService.updateTask(taskStatus);
        setTaskStatus(response.data.taskStatus);
      } catch (error) {
        console.error('Error fetching task status:', error);
        // Handle the error (e.g., show a user-friendly error message)
      }
    };

    fetchTaskStatus();
  }, [taskStatus]); // Dependency array ensures the effect runs when the ID changes

  const handleInputChange = (e) => {
    setTaskStatus(e.target.value);
  };

  const updateTaskStatus = async () => {
    try {
      await UserService.updateTask(taskStatus,updateTask);
      navigate('/'); // Redirect to the task list after updating the task status
    } catch (error) {
      console.error('Error updating task status:', error);
      // Handle the error (e.g., show a user-friendly error message)
    }
  };


  const cancel = () => {
    navigate('/'); // Redirect to the task list without updating the task status
  };
  return (
    <div>
      <h2 className="text-center">Update Task</h2>
      <form>
      <div className="form-group">
      <label>Id</label>
          <input
            type="text"
            placeholder="Id"
            name="id"
            className="form-control"
            value={taskStatus.id}
            onChange={handleInputChange}
          />
          <label>TaskName</label>
          <input
            type="text"
            placeholder="Task Name"
            name="taskName"
            className="form-control"
            value={taskStatus.taskName}
            onChange={handleInputChange}
          />
          <label>Description</label>
          <input
            type="text"
            placeholder="Description"
            name="description"
            className="form-control"
            value={taskStatus.description}
            onChange={handleInputChange}
          />
          <label>TaskStartDate</label>
          <input
            type="text"
            placeholder="TaskStartDate"
            name="startDate"
            className="form-control"
            value={taskStatus.startDate}
            onChange={handleInputChange}
          />
          <label>TaskEndDate</label>
          <input
            type="text"
            placeholder="TaskEndDate"
            name="endDate"
            className="form-control"
            value={taskStatus.endDate}
            onChange={handleInputChange}
          />
          <label>TotalEffortRequired</label>
          <input
            type="text"
            placeholder="TotalEffortRequired"
            name="totalEffortReq"
            className="form-control"
            value={taskStatus.totalEffortReq}
            onChange={handleInputChange}
          />
          <label>TaskStatus</label>
          <input
            type="text"
            placeholder="TaskStatus"
            name="taskStatus"
            className="form-control"
            value={taskStatus.taskStatus}
            onChange={handleInputChange}
          />
        </div>


        {/* Repeat similar blocks for other input fields */}

        <button type="button" className="btn btn-success" onClick={updateTaskStatus}>
          Update
        </button>
        <button type="button" className="btn btn-danger" onClick={cancel}>
          Cancel
        </button>
      </form>
    </div>
  );
};

export default UpdateUserComponent;
