import React, { useState, useEffect } from 'react';
import UserService from '../services/UserService';
import { useNavigate } from 'react-router-dom';

const ViewTaskComponent = ({ taskName }) => {
    const navigate = useNavigate();
    const buttonContainerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
      };
      const buttonStyle = {
        margin: '10px 20px',
        padding: '15px 20px',
        fontSize: '16px',
        borderRadius: '5px',
        border: 'none',
        cursor: 'pointer',
      };
    const buttonView = {
        ...buttonStyle,
    backgroundColor: '#3498db', // Blue color
    color: '#fff', // White text
      };

  const [task, setTask] = useState([]);

  const viewAllTask = () => {
    navigate(`/list/all`);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {

          // If taskIdentifier is not a number, fetch by taskName
         const response = await UserService.getTaskByTaskName(taskName);
         setTask(response.data);
        } catch (error) {
        console.error('Error fetching task:', error);
        // Handle error, e.g., show an error message to the user
      }
    };

    fetchData();
  }, [taskName]);

  return (
    <div>
      <h3>List of Items for User TaskName: {taskName}</h3>
      <ul>
        {task.map((tasklist) => (
          <li key={tasklist.taskName}>
            <strong>{tasklist.id}</strong>: {tasklist.description}
          </li>
        ))}
      </ul>

      <div style={buttonContainerStyle}>
      <div>
      <button style={buttonView} onClick={viewAllTask}>
        ViewAllTask</button>
      </div>
      </div>
    </div>
  );
};

export default ViewTaskComponent;
