import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const UpdateUserComponent = () => {
  const [taskStatus, setTaskStatus] = useState('');
  const navigate = useNavigate();
  const { taskStatus: currentTaskStatus } = useParams(); // Extract the taskStatus from the URL

  useEffect(() => {
    setTaskStatus(currentTaskStatus);
  }, [currentTaskStatus]);

  const handleInputChange = (e) => {
    setTaskStatus(e.target.value);
  };

  const updateTaskStatus = async () => {
    try {
      // Make the API call to update the task status
      const response = await fetch(`USER_API_BASE_URL/update/${currentTaskStatus}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          taskStatus: taskStatus,
        }),
      });

      // Check if the request was successful (status code 2xx)
      if (response.ok) {
        navigate('/'); // Redirect to the task list after updating the task status
      } else {
        console.error('Error updating task status:', response.statusText);
        // Handle the error (e.g., show a user-friendly error message)
      }
    } catch (error) {
      console.error('Error updating task status:', error);
      // Handle the error (e.g., show a user-friendly error message)
    }
  };

  const cancel = () => {
    navigate('/'); // Redirect to the task list without updating the task status
  };
  return (
    <div>
      <h2 className="text-center">Update Task</h2>
      <form>
      <div className="form-group">
      <label>Id</label>
          <input
            type="text"
            placeholder="Id"
            name="id"
            className="form-control"
            value={currentTaskStatus.id}
            onChange={handleInputChange}
          />
          <label>TaskName</label>
          <input
            type="text"
            placeholder="Task Name"
            name="taskName"
            className="form-control"
            value={currentTaskStatus.taskName}
            onChange={handleInputChange}
          />
          <label>Description</label>
          <input
            type="text"
            placeholder="Description"
            name="description"
            className="form-control"
            value={currentTaskStatus.description}
            onChange={handleInputChange}
          />
          <label>TaskStartDate</label>
          <input
            type="text"
            placeholder="TaskStartDate"
            name="startDate"
            className="form-control"
            value={currentTaskStatus.startDate}
            onChange={handleInputChange}
          />
          <label>TaskEndDate</label>
          <input
            type="text"
            placeholder="TaskEndDate"
            name="endDate"
            className="form-control"
            value={currentTaskStatus.endDate}
            onChange={handleInputChange}
          />
          <label>TotalEffortRequired</label>
          <input
            type="text"
            placeholder="TotalEffortRequired"
            name="totalEffortReq"
            className="form-control"
            value={currentTaskStatus.totalEffortReq}
            onChange={handleInputChange}
          />
          <label>TaskStatus</label>
          <input
            type="text"
            placeholder="TaskStatus"
            name="taskStatus"
            className="form-control"
            value={currentTaskStatus.taskStatus}
            onChange={handleInputChange}
          />
        </div>


        {/* Repeat similar blocks for other input fields */}

        <button type="button" className="btn btn-success" onClick={updateTaskStatus}>
          Update
        </button>
        <button type="button" className="btn btn-danger" onClick={cancel}>
          Cancel
        </button>
      </form>
    </div>
  );
};

export default UpdateUserComponent;
