import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import UserService from '../services/UserService';

const CreateUserComponent = () => {
  const [task, setTask] = useState({
    taskName: '',
    description: '',
    startDate: '',
    endDate: '',
    taskStatus: '',
    totalEffortReq: '',
  });

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };

  const saveTask = async () => {
    try {
      await UserService.createTask(task);
      navigate('/'); // Redirect to the task list after adding a task
    } catch (error) {
      console.error('Error adding task:', error);
      // Handle the error (e.g., show a user-friendly error message)
    }
  };

  const cancel = () => {
    navigate('/'); // Redirect to the task list without adding a task
  };

  return (
    <div>
      <br />
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3 offset-md-3">
            <h3 className="text-center">Update Task</h3>
            <div className="card-body">
              <form>
                {/* ... other form fields ... */}
                <div className="form-group">
                  <label> Id </label>
                  <input
                    placeholder="Id"
                    name="id"
                    className="form-control"
                    value={task.id}
                    onChange={handleChange}
                  />
                  <label> TaskName </label>
                  <input
                    placeholder="TaskName"
                    name="taskName"
                    className="form-control"
                    value={task.taskName}
                    onChange={handleChange}
                  />
                  <label> StartDate </label>
                  <input
                    placeholder="StartDate"
                    name="startDate"
                    className="form-control"
                    value={task.startDate}
                    onChange={handleChange}
                  />
                  <label> EndDate </label>
                  <input
                    placeholder="EndDate"
                    name="endDate"
                    className="form-control"
                    value={task.endDate}
                    onChange={handleChange}
                  />
                  <label> TaskStatus </label>
                  <input
                    placeholder="TaskStatus"
                    name="taskStatus"
                    className="form-control"
                    value={task.taskStatus}
                    onChange={handleChange}
                  />
                  <label> TotalEffortReq </label>
                  <input
                    placeholder="TotalEffortReq"
                    name="totalEffortReq"
                    className="form-control"
                    value={task.totalEffortReq}
                    onChange={handleChange}
                  />
                </div>

                <button className="btn btn-success" onClick={updateTask}>
                  Save
                </button>
                <button
                  className="btn btn-danger"
                  onClick={cancel}
                  style={{ marginLeft: '10px' }}
                >
                  Cancel
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateUserComponent;
