import React, { Component } from 'react'
import UserService from '../services/UserService';

class CreateUserComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            // step 2
            id: this.props.match.params.id,
            TaskName: '',
            Description: '',
            StartDate: '',
            EndDate: '',
            TaskStatus: '',
            TotalEffortReq:''
        }
        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
        this.saveOrUpdateUser= this.saveOrUpdateUser.bind(this);
    }

    // step 3
    componentDidMount(){

        // step 4
        if(this.state.id === '_add'){
            return
        }else{
            UserService.getTaskByTaskName(this.state.id).then( (res) =>{
                let task = res.data;
                this.setState({Id: task.id, TaskName: task.taskName, Description:task.description, StartDate: task.startDate, EndDate: task.endDate, TaskStatus: task.taskStatus, TotalEffortReq: task.totalEffortReq

                });
            });
        }        
    }
    saveOrUpdateUser = (e) => {
        e.preventDefault();
        let task = {Id: this.state.id, TaskName: this.state.TaskName, Description:this.state.Description, StartDate: this.state.StartDate, EndDate: this.state.EndDate, TaskStatus: this.state.TaskStatus, TotalEffortReq: this.state.TotalEffortReq};
        console.log('task => ' + JSON.stringify(task));

        // step 5
        if(this.state.id === '_add'){
            UserService.createUser(task).then(res =>{
                this.props.history.push('/add-list');
            });
        }else{
            UserService.updateUser(task, this.state.id).then( res => {
                this.props.history.push('/update/{taskStatus}');
            });
        }
    }

    changeFirstNameHandler= (event) => {
        this.setState({firstName: event.target.value});
    }

    changeLastNameHandler= (event) => {
        this.setState({lastName: event.target.value});
    }

    changeEmailHandler= (event) => {
        this.setState({emailId: event.target.value});
    }

    cancel(){
        this.props.history.push('/list/all');
    }

    getTitle(){
        if(this.state.id === '_add'){
            return <h3 className="text-center">Add Employee</h3>
        }else{
            return <h3 className="text-center">Update Employee</h3>
        }
    }
    render() {
        return (
            <div>
                 <label> TaskName: </label>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                {
                                    this.getTitle()
                                }
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> TaskName: </label>
                                            <input placeholder="TaskName" name="taskName" className="form-control" 
                                                value={this.state.TaskName} onChange={this.changeTaskNameHandler}/>
                                        </div>


                                        <button className="btn btn-success" onClick={this.saveOrUpdateTask}>Save</button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default CreateUserComponent;
