import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import UserService from '../services/UserService';

const UpdateUserComponent = () => {
  const [task, setTask] = useState({
    taskName: '',
    description: '',
    startDate: '',
    endDate: '',
    taskStatus: '',
    totalEffortReq: '',
  });

  const navigate = useNavigate();
  const { taskStatus } = useParams();

  useEffect(() => {
    // Fetch the existing task data when the component mounts
    const fetchTask = async () => {
      try {
        const response = await UserService.findByTaskStatus(taskStatus);
        setTask(response.data);
      } catch (error) {
        console.error('Error fetching task:', error);
        // Handle the error (e.g., show a user-friendly error message)
      }
    };

    fetchTask();
  }, [id]); // Dependency array ensures the effect runs when the ID changes

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };

  const updateTask = async () => {
    try {
      await UserService.findByTaskStatus(taskStatus, task);
      navigate('/'); // Redirect to the task list after updating a task
    } catch (error) {
      console.error('Error updating task:', error);
      // Handle the error (e.g., show a user-friendly error message)
    }
  };

  const cancel = () => {
    navigate('/'); // Redirect to the task list without updating the task
  };

  return (
    <div>
      <h2 className="text-center">Update Task</h2>
      <form>
      <div className="form-group">
          <label>TaskName</label>
          <input
            type="text"
            placeholder="Task Name"
            name="taskName"
            className="form-control"
            value={task.taskName}
            onChange={handleInputChange}
          />
          <label>Description</label>
          <input
            type="text"
            placeholder="Description"
            name="description"
            className="form-control"
            value={task.description}
            onChange={handleInputChange}
          />
          <label>TaskStartDate</label>
          <input
            type="text"
            placeholder="TaskStartDate"
            name="startDate"
            className="form-control"
            value={task.startDate}
            onChange={handleInputChange}
          />
          <label>TaskEndDate</label>
          <input
            type="text"
            placeholder="TaskEndDate"
            name="endDate"
            className="form-control"
            value={task.endDate}
            onChange={handleInputChange}
          />
          <label>TotalEffortRequired</label>
          <input
            type="text"
            placeholder="TotalEffortRequired"
            name="totalEffortReq"
            className="form-control"
            value={task.totalEffortReq}
            onChange={handleInputChange}
          />
          <label>TaskStatus</label>
          <input
            type="text"
            placeholder="TaskStatus"
            name="taskStatus"
            className="form-control"
            value={task.taskStatus}
            onChange={handleInputChange}
          />
        </div>


        {/* Repeat similar blocks for other input fields */}

        <button type="button" className="btn btn-success" onClick={updateTask}>
          Update
        </button>
        <button type="button" className="btn btn-danger" onClick={cancel}>
          Cancel
        </button>
      </form>
    </div>
  );
};

export default UpdateUserComponent;
