import React, { useState, useEffect } from 'react';
import UserService from '../services/UserService';

const ListByTaskName = () => {
    const [selectedTaskName, setSelectedTaskName] = useState('');
    const [taskDetails, setTaskDetails] = useState(null);

    useEffect(() => {
        const fetchTaskDetails = async () => {
          if (selectedTaskName) {
            try {
              const response = await UserService.getTaskByTaskName(encodeURIComponent(selectedTaskName));
              setTaskDetails(response.data);
            } catch (error) {
              console.error('Error fetching task details:', error);
              // Handle error, e.g., show an error message to the user
            }
          }
        };

        fetchTaskDetails();
      }, [selectedTaskName]);

return (
   <div>
      {taskDetails && (
      <div>
      <h4>###### Details for Task #######</h4>
      <p>id: {taskDetails.id}</p>
      <p>TaskName: {taskDetails.taskName}</p>
      <p>StartDate: {taskDetails.startDate}</p>
      <p>EnDate: {taskDetails.endDate}</p>
      <p>TaskStatus: {taskDetails.taskStatus}</p>
      <p>TotalEffortReq: {taskDetails.totalEffortReq}</p>
     </div>
     )}
    </div>
  );
};
  export default ListByTaskName;
