import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import UserService from '../services/UserService';

const ListUserComponent = () => {
  const [tasks, setTasks] = useState([]);
  const history = useHistory();

  const addTask = () => {
    history.push('/add-list');
  };

  const viewTask = (id) => {
    history.push(`/list/all`);
  };

  const editTask = (id) => {
    history.push(`/add-task`);
  };

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await UserService.getAllTasks();
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []); // Empty dependency array means this effect runs once when the component mounts

  return (
    <div>
      <h2 className="text-center">Task List</h2>
      <div className="row">
        <button className="btn btn-primary" onClick={addTask}>
          Add Task
        </button>
      </div>
      <br></br>
      <div className="row">
        <table className="table table-striped table-bordered">
          <thead>
            <tr>
              <th>Id</th>
              <th>TaskName</th>
              <th>Description</th>
              <th>StartDate</th>
              <th>EndDate</th>
              <th>TaskStatus</th>
              <th>TotalEffortReq</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.id}>
                <td>{task.id}</td>
                <td>{task.taskName}</td>
                <td>{task.description}</td>
                <td>{task.startDate}</td>
                <td>{task.endDate}</td>
                <td>{task.taskStatus}</td>
                <td>{task.totalEffortReq}</td>
                <td>
                  <button onClick={() => editTask(task.id)} className="btn btn-info">
                    Update
                  </button>
                  <button
                    style={{ marginLeft: '10px' }}
                    onClick={() => viewTask(task.id)}
                    className="btn btn-info"
                  >
                    View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListUserComponent;
