import React, { Component } from 'react'
import UserService from '../services/UserService'

class ListUserComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
                task: []
        }
        this.addTask = this.addTask.bind(this);
        this.editTask = this.editTask.bind(this);
        this.viewTask = this.viewTask.bind(this);
    }
    addTask = (event) => {
        console.log(this.props);
        event.preventDefault();
         this.props.history.push('/add-list');
   }
    viewTask(id){
        this.props.history.push(`/list/all/${id}`);
    }
    editTask(id){
        this.props.history.push(`/add-task/${id}`);
    }

    componentDidMount(){
        UserService.getAllTasks().then((res) => {
            this.setState({ task: res.data});
        });
    }


    render() {
        return (
            <div>
                 <h2 className="text-center">Task List</h2>
                 <div className = "row">
                    <button className="btn btn-primary" onClick={this.addTask}>Add Task</button>
                 </div>
                 <br></br>
                 <div className = "row">
                        <table className = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th>Id</th>
                                    <th>TaskName</th>
                                    <th>Description</th>
                                    <th>StartDate</th>
                                    <th>EndDate</th>
                                    <th>TaskStatus</th>
                                    <th>TotalEffortReq</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.task.map(
                                        task => 
                                        <tr key = {task.id}>
                                            <td>{task.id}</td>
                                             <td> { task.taskName} </td>   
                                             <td> {task.description}</td>
                                             <td> {task.startDate}</td>
                                             <td> {task.endDate}</td>
                                             <td> {task.taskStatus}</td>
                                             <td> {task.totalEffortReq}</td>
                                             <td>
                                                 <button onClick={ () => this.editTask(task.id)} className="btn btn-info">Update</button>
                                                <button style={{marginLeft: "10px"}} onClick={ () => this.viewTask(task.id)} className="btn btn-info">View</button>
                                             </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>

                 </div>

            </div>
        )
    }
}

export default ListUserComponent;
